public class Exec8HexOctalLongMethodTest {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		long octal = 077777l;
		long hexadecimal = 0xffffl;
		System.out.println("octal in binary : " + Long.toBinaryString(octal));
		System.out.println("hexadecimal in binary : " + Long.toBinaryString(hexadecimal));
	}

}
